public class LC567 {

    public static void main(String[] args) {
        // example
        String s1 = "";
        String s2 = "";
        System.out.println(
            new LC567().checkInclusion(s1, s2)
        );
    }

    public boolean checkInclusion(String s1, String s2) {
        if (s1.length() > s2.length()) {
            return false;
        }

        Map<Character, Integer> s1Map = new HashMap<>(26);
        Map<Character, Integer> s2Map = new HashMap<>(26);

        int maxChar = 97+26;

        for(int i = 97; i < maxChar; i++) {
            s1Map.put((char) i, 0);
            s2Map.put((char) i, 0);
        }

        for (int i = 0;  i < s1.length(); i++) {
            s1Map.put(s1.charAt(i), s1Map.get(s1.charAt(i)) + 1);
            s2Map.put(s2.charAt(i), s2Map.get(s2.charAt(i)) + 1);
        }

        int matches = 0;

        for(int i = 97; i < maxChar; i++) {
            char charAti = (char) i;
            System.out.println(s1Map.get(charAti) + " " + s2Map.get(charAti));
            int s1charati = s1Map.get(charAti);
            int s2charati = s2Map.get(charAti);

            if(s1charati == s2charati) {
                matches += 1;
            }
        }


        int left = 0;
        for(int right = s1.length(); right < s2.length(); right++) {
            if(matches == 26) {
                return true;
            }

            // add right to s2Map
            char charAtRight = s2.charAt(right);

            s2Map.put(charAtRight, s2Map.get(charAtRight) + 1);
            if((int)s1Map.get(charAtRight) == (int)s2Map.get(charAtRight)) {
                matches += 1;
            } else if ((int)s1Map.get(charAtRight) + 1 == (int) s2Map.get(charAtRight)) {
                matches -= 1;
            }

            char charAtLeft = s2.charAt(left);

            s2Map.put(charAtLeft, s2Map.get(charAtLeft) - 1);
            if((int) s1Map.get(charAtLeft) == (int)s2Map.get(charAtLeft)) {
                matches += 1;
            } else if ((int)s1Map.get(charAtLeft) - 1 == (int) s2Map.get(charAtLeft)) {
                matches -= 1;
            }
            left += 1;
        }
        return matches == 26;
    }
}